// To install dependencies:
//   cargo add eframe walkdir anyhow
//
// Then run with:
//   cargo run

use anyhow::Result;
use eframe::{egui, App};
use egui::ViewportBuilder;
use egui_extras::{Column, TableBuilder};
use std::collections::HashSet;
use std::fs;
use walkdir::WalkDir;

fn main() -> eframe::Result<()> {
    let options = {
        let mut opt = eframe::NativeOptions::default();
        let viewport_build = ViewportBuilder::default();
        opt.viewport = viewport_build.with_inner_size((800.0, 600.0));
        opt
    };

    eframe::run_native(
        "Text Files Incidence Matrix",
        options,
        Box::new(|_cc| Box::new(MyApp::new("."))),
    )
}

struct MyApp {
    files: Vec<String>,
    words: Vec<String>,
    incidence: Vec<Vec<bool>>,
}

impl MyApp {
    fn new(root: &str) -> Self {
        let (f, w, im) = build_incidence_matrix(root);
        Self {
            files: f,
            words: w,
            incidence: im,
        }
    }
}

impl App for MyApp {
    fn update(&mut self, ctx: &egui::Context, _frame: &mut eframe::Frame) {
        egui::CentralPanel::default().show(ctx, |ui| {
            ui.heading("Incidence Matrix from Text Files");
            ui.label("Rows: unique words. Columns: text files. 'X' if present, '.' if absent.");

            egui::ScrollArea::both().show(ui, |ui| {
                TableBuilder::new(ui)
                    .striped(true)
                    .cell_layout(egui::Layout::left_to_right(egui::Align::Center))
                    .column(Column::auto().at_least(150.0).resizable(true))
                    .columns(
                        Column::auto().at_least(80.0).resizable(true),
                        self.files.len(),
                    )
                    .header(20.0, |mut header| {
                        header.col(|ui| {
                            ui.strong("Word");
                        });
                        for file in &self.files {
                            header.col(|ui| {
                                ui.strong(file);
                            });
                        }
                    })
                    .body(|mut body| {
                        for (row_idx, word) in self.words.iter().enumerate() {
                            body.row(18.0, |mut row| {
                                row.col(|ui| {
                                    ui.label(word);
                                });
                                for (col_idx, _) in self.files.iter().enumerate() {
                                    row.col(|ui| {
                                        ui.label(if self.incidence[row_idx][col_idx] {
                                            "X"
                                        } else {
                                            "."
                                        });
                                    });
                                }
                            });
                        }
                    });
            });
        });
    }
}

// Build incidence matrix of unique words vs. text files
fn build_incidence_matrix(root: &str) -> (Vec<String>, Vec<String>, Vec<Vec<bool>>) {
    let mut text_files = Vec::new();
    for entry in WalkDir::new(root)
        .follow_links(false)
        .into_iter()
        .filter_map(Result::ok)
    {
        if entry.file_type().is_file() {
            if let Some(ext) = entry.path().extension() {
                if ext == "txt" {
                    text_files.push(entry.path().to_owned());
                }
            }
        }
    }
    if text_files.is_empty() {
        return (Vec::new(), Vec::new(), Vec::new());
    }

    // We want a set of all unique words from all .txt files
    let mut global_words_set = HashSet::new();
    let mut file_words: Vec<HashSet<String>> = Vec::new();

    for file in &text_files {
        let text = fs::read_to_string(file).unwrap_or_default();
        let tokens = tokenize(&text);
        let file_set: HashSet<_> = tokens.into_iter().collect();
        // Merge into global set
        for w in &file_set {
            global_words_set.insert(w.clone());
        }
        file_words.push(file_set);
    }

    // Build a stable list from the set
    let mut words_list: Vec<String> = global_words_set.into_iter().collect();
    words_list.sort();

    // Build incidence: rows = words, columns = files
    let mut incidence_vec = vec![vec![false; text_files.len()]; words_list.len()];

    for (col_idx, fset) in file_words.iter().enumerate() {
        for (row_idx, w) in words_list.iter().enumerate() {
            if fset.contains(w) {
                incidence_vec[row_idx][col_idx] = true;
            }
        }
    }

    // Convert file paths to strings
    let file_names: Vec<String> = text_files
        .into_iter()
        .map(|f| {
            if let Some(name) = f.file_name() {
                name.to_string_lossy().to_string()
            } else {
                f.to_string_lossy().to_string()
            }
        })
        .collect();

    (file_names, words_list, incidence_vec)
}

// Simple tokenizer to split string into alphanumeric words
fn tokenize(text: &str) -> Vec<String> {
    // We can split on non-alphanumeric characters
    text.split(|c: char| !c.is_alphanumeric())
        .filter(|s| !s.is_empty())
        .map(|s| s.to_lowercase())
        .collect()
}
